pragma solidity ^0.4.13;

import "./Controlled.sol";
import "./Controller.sol";

contract Token is Controlled { //El token está controlado por alguien, en este caso por ICO
    mapping (address => uint256) balances; //cantidad de tokens por address (compradores)
    uint256 public totalSupply; //contador total

    constructor() public { // el que llame será el controlador (debeía ser ICO ico.address)
        controller = msg.sender;
    }

    //LOG para transfers
    event TransferLog (address indexed _from, address indexed _to, uint256 _value);

    function balanceOf(address _owner) public view returns (uint256 balance) { //saber para un owner su balance
        if (balances[_owner] == 0) {
            return 0;
        } else {
            return balances[_owner];
      }
    }

    function transfer(address _to, uint256 _value) public returns (bool success) { //transfiere des de cuenta que llama (message sender) al ICO
        return doTransfer(msg.sender, _to, _value);
    }

    function transferFrom(address _from, address _to, uint256 _value) public  onlyController returns (bool success) { 
                                                              //transfiere enviando el message sender, sólo gestiona controller
        return doTransfer(_from, _to, _value);
    }

    function doTransfer (address _from, address _to, uint256 _value) internal returns (bool) {
        if (_value == 0) {
            return true;
        }
        require ((_to != 0) && (_to != address(this)));
        uint previousBalanceFrom = balanceOf(_from); //recuperamos balance de _from
        if (previousBalanceFrom < _value) { //comprobamos que el balance del from sea mayor que value
            return false;
        }
        balances[_from] = balances[_from] - _value; //restamos al balance el valor _from
        uint previousBalanceTo = balanceOf(_to); //recuperamos balance de _to
        require(previousBalanceTo + _value >= previousBalanceTo); //No queremos overflow, comprobamos que el balance del to sea mayor que value
        balances[_to] = balances[_to] + _value; //sumamos al balance el valor de  _to
        emit TransferLog(_from, _to, _value); //sólo para saber quien lo ha realizado, log en event
    }

    function isContract(address _addr) view internal returns(bool) { //helper para comprobar si una address es un contrato.
        uint size;
        if (_addr == 0) {
            return false;
        }

        assembly {  size := extcodesize(_addr) 
        }
        
        return size>0;
    }

    function () public payable { //método fallback, va cualquier transacción que vaya a una signature inexistente
        require(isContract(controller)); // comprobamos que sea un contrato
        Controller c = Controller(controller); // llamamos constructor de controller
        require(c.proxyPayment.value(msg.value)(msg.sender)); //llamamos método payable proxyPayment
                                                         //reenviamos el value dentro de la transacción 
    }

    function generateTokens(address _owner, uint _amount) public onlyController returns (bool) { //genera el token requerido
        uint curTotalSuply = totalSupply;
        require(curTotalSuply + _amount >= curTotalSuply);
        uint previousBalanceTo = balanceOf(_owner);
        require(previousBalanceTo + _amount >= previousBalanceTo); //No queremos overflow, comprobamos que el balance del to sea mayor que value
        totalSupply = curTotalSuply + _amount;
        balances[_owner] = previousBalanceTo + _amount;
        emit TransferLog(0, _owner, _amount); //sólo para saber quien lo ha realizado, log en event
        return true;
    }

   

}
